export function resolveExtensionApi(config) {
  return {
    name: "wxt:resolve-extension-api",
    config() {
      if (config.extensionApi === "webextension-polyfill") return;
      return {
        resolve: {
          alias: [
            { find: /^wxt\/browser$/, replacement: "wxt/browser/chrome" }
          ]
        }
      };
    }
  };
}
